args:
/* Loader Animations */
@keyframes fade-cycle {
    0%, 100% { opacity: 0; transform: scale(0.8); }
    10%, 90% { opacity: 1; transform: scale(1); }
}

.animate-fade-cycle {
    animation: fade-cycle 4.5s infinite;
}

@keyframes text-slide-up {
    0%, 20% { transform: translateY(0); }
    25%, 45% { transform: translateY(-100%); }
    50%, 70% { transform: translateY(-200%); }
    75%, 95% { transform: translateY(-300%); }
    100% { transform: translateY(-400%); } /* Reset point if needed, or adjust keyframes */
}

/* 
   We have 3 messages. 
   0% -> Msg 1
   33% -> Msg 2
   66% -> Msg 3
   100% -> Msg 1 (Loop)
*/
@keyframes text-carousel {
    0%, 25% { transform: translateY(0); }
    33%, 58% { transform: translateY(-24px); } /* Assuming 24px height (h-6) */
    66%, 91% { transform: translateY(-48px); }
    100% { transform: translateY(-72px); } /* Move to cloned first item then reset instantly */
}

.animate-text-carousel {
    animation: text-carousel 9s cubic-bezier(0.4, 0, 0.2, 1) infinite;
}

/* Stack Card Styles */
.stack-card {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 1rem;
    background-color: #1a2332; /* surface-dark */
    border: 1px solid rgba(255, 255, 255, 0.1);
    overflow: hidden;
    transition: transform 0.3s cubic-bezier(0.25, 0.8, 0.25, 1), opacity 0.3s ease;
    box-shadow: 0 10px 30px -10px rgba(0, 0, 0, 0.5);
    touch-action: none; /* Important for drag */
    cursor: grab;
}

.stack-card:active {
    cursor: grabbing;
}

/* Stack depth effect */
.stack-card:nth-child(1) {
    z-index: 3;
    transform: translateY(0) scale(1);
}
.stack-card:nth-child(2) {
    z-index: 2;
    transform: translateY(10px) scale(0.95);
    opacity: 0.8;
}
.stack-card:nth-child(3) {
    z-index: 1;
    transform: translateY(20px) scale(0.90);
    opacity: 0.6;
}
.stack-card:nth-child(n+4) {
    z-index: 0;
    transform: translateY(30px) scale(0.85);
    opacity: 0;
    pointer-events: none;
}

/* Fly out animations */
.fly-out-left {
    transform: translate(-120%, 20px) rotate(-20deg) !important;
    opacity: 0 !important;
    transition: transform 0.4s ease-in, opacity 0.4s ease-in;
}
.fly-out-right {
    transform: translate(120%, 20px) rotate(20deg) !important;
    opacity: 0 !important;
    transition: transform 0.4s ease-in, opacity 0.4s ease-in;
}
